SELECT 
  * 
FROM 
  public.compromisoinicialdetalle, 
  public.compromisoinicial, 
  public.partidauelespecifica, 
  public.uelespecifica, 
  public.unidadejecutora
WHERE 
  compromisoinicial.id_compromiso_inicial = compromisoinicialdetalle.id_compromiso_inicial AND
  partidauelespecifica.id_partida_uel_especifica = compromisoinicialdetalle.id_partida_uel_especifica AND
  uelespecifica.id_uel_especifica = partidauelespecifica.id_uel_especifica AND
  unidadejecutora.id_unidad_ejecutora = uelespecifica.id_unidad_ejecutora AND
  compromisoinicial.expediente = 6;


 	where 


				#idCompromisoInicialDetalle[]#
